                [/([ti])um$/gi, "$1a"],
                [/sis$/gi, "ses"],
                [/(?:([^f])fe|([lr])f)$/gi, "$1$2ves"],
                [/(hive)$/gi, "$1s"],
                [/([^aeiouy]|qu)y$/gi, "$1ies"],
