export class RestRequest {
    /**
     *
     * @param method
     * @param model
     * @param options
     */
    constructor(method, model, options) {
        Object.defineProperty(this, "method", {
            get: () => method,
            enumerable: true,
            configurable: false,
        });

        Object.defineProperty(this, "model", {
            get: () => model,
            enumerable: true,
            configurable: false,
        });

        Object.defineProperty(this, "options", {
            get: () => Object.assign({cookie: {}, headers: {}, path: {}, query: {}}, options),
            enumerable: true,
            configurable: false,
        });

        const _opElement = model.$schema.operations[method.toLowerCase()];

        Object.defineProperty(this, "parameters", {
            value: new RequestParams(model, _opElement.parameters || [], this.options),
            enumerable: true,
            configurable: false,
        });

    }

    /**
     *
     * @returns {*}
     */
    get url() {
        let _query = "";
        if (this.model.hasOwnProperty("url")) {
            return this.model.url;
        }

        const _t = template["parse"](this.model["schemaPath"]);
        const _path = _t.expand(Object.assign({}, this.pathValues));
        const _qParams = this.options.query;
        if (Object.keys(_qParams || {}).length) {
            _query = `?${Obj.objectToQuery(_qParams)}`;
        }

        return `${this.model.$scope.apiUrl}${_path}${_query}`;
    }

    /**
     *
     */
    get pathValues() {
        const __ = {};

        if (this.model["isModel"] && this.model["pathIDKey"] !== null) {
            const _idKey = `${inflection.underscore(this.model.pathIDKey)}`.split("_").pop();
            const _keys = this.parameters.path.propertyNames;
            _keys[_keys.indexOf(this.model.pathIDKey)] = _idKey;
            _keys.forEach((k) => {
                __[k === _idKey ? this.model.pathIDKey : k] = this.model.data[k]
            });
        }

        return __;
    }

    /**
     *
     * @returns {RestResponse}
     */
    exec() {
        let opts = this.model.$scope.$utils.apiOptions;
        const _cookie = Obj.objectToQuery(this.options.cookie).replace("&", ";");
        Object.assign(opts, this.options, this.options.headers, {Cookie: _cookie, method: this.method});

        const _reqObj = {
            request: () => {
                return new Promise((resolve, reject) => {
                    fetch(this.url, opts)
                        .then((d) => resolve(d))
                        .catch((e) => reject(e));
                })
            },
            options: opts,
            follow: (url) => {
                const _model = {
                    url: url,
                    $scope: this.model.$scope,
                    $schema: this.model.$schema,
                };
                delete opts.body;
                return new RestRequest("GET", _model, opts);
            },
        };

        // handle Fetch's Promise and update Rx Subject
        return new RestResponse(_reqObj);
    }
}

import {BehaviorSubject} from "rxjs";

const isFetchPolyfill = () => {
    const _env = (global || window);
    return _env.fetch.hasOwnProperty("polyfill");
};

export class RestResponse {
    constructor(requestObj) {
        const _subj = new BehaviorSubject().skip(1);
        Object.defineProperty(this, "subscribe", {
            value: (handlers) => {
                return _subj.subscribe(handlers);
            },
            enumerable: true,
            configurable: false,
        });

        requestObj.request().then((res) => {
            // nodejs uses a fetch polyfill which requires manual handling of location header
            if (isFetchPolyfill) {
                const _location = res.headers.get("location");
                // detects if we have a url to follow
                if (_location !== null) {
                    // creates and invokes a request object for the location request
                    requestObj.follow(_location).exec().subscribe({
                        next: (res) => {
                            // sends response data to subscribers from redirect
                            _subj.next(res);
                        },
                        error: (e) => {
                            // sends error to subscriber from redirect
                            _subj.error(e);
                        }
                    });
                } else {
                    // sends response data to subscribers from request origin
                    _subj.next(res);
                }
            } else {
                // sends response data to subscribers from request origin
                _subj.next(res);
            }
        }).catch((e) => {
            // catch all handler on bad implementation
            _subj.error(e);
        });
    }
}

import {Utils} from "./utils";
const _namespaces = new WeakMap();

export class SchemaBuilder {
    constructor(ns) {
        _namespaces.set(this, ns);
    }

    /**
     *
     * @returns {any}
     */
    get namespace() {
        return _namespaces.get(this);
    }

    /**
     *
     * @param schemaPathElement
     */
    getOperations(schemaPathElement) {
        const _ops = {};
        const _s = (this.namespace.schemaType.type === "openapi") ?
            schemaPathElement : schemaPathElement.operations;
        Object.keys(_s).forEach((op) => {
            const _opSchema = _s[op];
            _ops[op] = (this.namespace.schemaType.type === "openapi") ?
                _opSchema :
                Utils.reformatV2Response(_s[op]);
        });

        return _ops;
    };



    /**
     *
     * @param forPath
     */
    schemaForPath(forPath) {
        const _schema = this.namespace.schema.$model.toJSON();

        const _rx = new RegExp(`^${forPath}+`);
        const _p = {};
        let elName;

        // creates a sorted array of matching paths/subpaths
        const _pKeys = Object.keys(_schema.paths).filter((pK) => _rx.exec(pK) !== null);
        _pKeys.sort();

        // traverses paths to build schema object
        _pKeys.forEach((path) => {
            elName = forPath.split("/").pop();
            const _parts = path.split("/");
            const _op = this.getOperations({operations: _schema.paths[path]});
            if (path === forPath) {
                _p[elName] = {
                    name: elName,
                    operations: _op.hasOwnProperty("operations") ? _op.operations : {},
                    path: path,
                    components: this.namespace.schema.components,
                    childPaths: {}
                }
            } else {
                if (`${_parts}` !== "") {
                    const _mPathName = path.split("/").pop();
                    if (!_p[elName].childPaths.hasOwnProperty(_mPathName)) {
                        Object.defineProperty(_p[elName].childPaths, _mPathName, {
                            value: Object.assign({}, {name: _mPathName, path: path}, _op),
                            configurable: false,
                            enumerable: true,
                        });
                    }
                }
            }
        });
        return _p.hasOwnProperty(elName) ? _p[elName] : {};
    }
}

