        at(idx) {
            if (idx < 0) idx += this.this.length;
            return this.models[idx];
        }

        /**
         * Retrieves model with matching id
         * @param id
         * @returns {*}
         */
        findById(id) {
            return this.models.find((m) => m.id === id);
        }

        /**
         * Resets collection to empty `Array`
         * @returns {Collection}
         */
        reset() {
            this.models.splice(0, this.models.length);
            return this;
        }

